/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 FUNCTION `fGetPaymentsPerOrder`(
pOrderID varchar(20)
) RETURNS text CHARSET latin1
BEGIN
return (
select group_concat(payment_type, ' : ', amount, `change`, `credit`, `approval`, `coupon` separator '\r\n') from
(select 
payment_type,
format(sum(amount), 2) as amount,
if(payment_type='CASH', concat('\r\nChange: ',
if((select sum(amount) from payment where ORDER_ID = pOrderID and `status` <>'VOID' group by ORDER_ID) > `order header`.TOTAL_AMOUNT,
format(((select sum(amount) from payment where ORDER_ID = pOrderID and `status` <>'VOID' group by ORDER_ID) - `order header`.TOTAL_AMOUNT),2),0.00)), '') as `Change`,
IF(PAYMENT_TYPE = 'Credit' OR PAYMENT_TYPE = 'DEBIT',CONCAT('\r\nApproval Code: ',APPROVAL_CODE),'') AS `Credit`,
if(PAYMENT_TYPE = 'Coupon',concat('\r\nCoupon Type: ',Coupon_Type),'') as `Approval`,
if(PAYMENT_TYPE = 'Coupon',concat('\r\nCoupon No: ',COUPON_NO),'') as `Coupon`
from payment 
left join `order header` on
`order header`.ORDER_ID = payment.ORDER_ID
where payment.ORDER_ID = pOrderID and payment.status <> 'VOID' group by payment_type) t);
END */;;
