/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`%`*/ /*!50003 PROCEDURE `spGenerateDRR`(
	pHotelId int(4),
	pStartDate date,
	pEndDate date
)
BEGIN
	
  DECLARE done INT DEFAULT 0;
  DECLARE drrScript VARCHAR(10000);
