  set @sqlString = concat(
	'(select date(trans.transactiondate) as DATE, trans.memo as MEMO, trans.transactioncode as `TRANS CODE`, trans.acctSide as `ACCT SIDE`, ', drrScript 
	,' trans.transactionSource as `TRANS SRC` '
