/*!50003 DROP PROCEDURE IF EXISTS `spGetAccumulatedSalesValueXZ` */;;
/*!50003 SET SESSION SQL_MODE="NO_AUTO_VALUE_ON_ZERO"*/;;
/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`%`*/ /*!50003 PROCEDURE `spGetAccumulatedSalesValueXZ`(
pDate DATE,
pTerminalId INT(4)
)
BEGIN
SELECT 
  ROUND(SUM(d.`UNIT_PRICE` * d.`QUANTITY`),2) AS gross,
  SUM(round(d.`DISCOUNT`,2)) AS discount,
  ROUND(SUM(d.`SERVICE_CHARGE`),2) AS sc,
  ROUND(SUM(d.`VAT`),2) AS gTax,
  ROUND(SUM(d.`LOCAL_TAX`),2) AS lTax,
  
  ROUND(SUM(d.`AMOUNT` + d.`VAT` + d.`LOCAL_TAX` + d.`SERVICE_CHARGE`),2) AS netSales, 
  ROUND(SUM(d.`AMOUNT` - d.`VAT_EXEMPT_SENIOR`),2) AS vatSales, 
  ROUND(SUM(d.`VAT_EXEMPT_SENIOR`), 2) AS nonVatSales 
  FROM `order header` h 
  INNER JOIN `order detail` d 
    ON h.`ORDER_ID` = d.`ORDER_ID` 
    AND h.`TERMINAL_ID` = pTerminalId 
    AND h.`STATUS` = 'SERVED' 
    AND d.`OPERATION_STATUS` = 'NEW' 
    where pDate >= date(h.ORDER_DATE);
   END */;;
