/*!50003 SET SESSION SQL_MODE="NO_AUTO_VALUE_ON_ZERO"*/;;
/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`%`*/ /*!50003 PROCEDURE `spGetDailyPaymentsBreakdownPerShiftXZ`(
pDate date,
pTerminalId int(4),
pShiftId int(4)
)
BEGIN
SELECT sql1.`PAYMENT_TYPE`,
       round(SUM(sql1.amount), 2) AS amount
FROM
(
SELECT
h.`ORDER_ID`,
p.`PAYMENT_TYPE`,
IFNULL(IF(p.`PAYMENT_TYPE` = 'CASH', (p.`AMOUNT` + (h.`BALANCE`)), p.`AMOUNT` ),0) AS amount
FROM `order header` h
INNER JOIN `payment` p
ON h.`ORDER_ID` = p.`ORDER_ID`
AND h.`STATUS` = 'SERVED'
AND p.`STATUS` = 'OK'
WHERE DATE(h.`ORDER_DATE`) = pDate
AND h.`TERMINAL_ID` = pTerminalId
AND h.`SHIFT_CODE` = pShiftId) AS sql1 GROUP BY sql1.`PAYMENT_TYPE`;
   END */;;
