/*!50003 SET SESSION SQL_MODE=@OLD_SQL_MODE*/;;
/*!50003 DROP PROCEDURE IF EXISTS `spGetDailySalesPerGroupXZ` */;;
/*!50003 SET SESSION SQL_MODE="NO_AUTO_VALUE_ON_ZERO"*/;;
/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`%`*/ /*!50003 PROCEDURE `spGetDailySalesPerGroupXZ`(
pDate DATE,
pTerminalId INT(4)
)
BEGIN
SELECT sql1.groupId, sql1.terminalId, sql1.shiftId, sql1.cashier, ROUND(sql1.gross, 2) AS gross, 
	ROUND(sql1.itemSold, 0) AS itemSold, sql1.discount, sql1.sc, 
	sql1.gTax, sql1.lTax,  ROUND(netSales, 2) AS netSales
FROM
(
SELECT 
      IF(d.`item_id` LIKE 'F%', 'FUNCTION', ci.`groupId`) AS groupId,
      h.`TERMINAL_ID` AS terminalId,
      h.`SHIFT_CODE` AS shiftId,
      h.`UPDATEDBY` AS cashier,
      sum(d.`AMOUNT` + d.`VAT` + d.`LOCAL_TAX` + d.`SERVICE_CHARGE`) as netSales, 
      SUM(d.`UNIT_PRICE` * d.`QUANTITY`) AS gross,
      SUM(d.`QUANTITY`) AS itemSold,
      SUM(round(d.`DISCOUNT`,2)) AS discount,
      SUM(d.`SERVICE_CHARGE`) AS sc,
      SUM(d.`VAT`) AS gTax,
      SUM(d.`LOCAL_TAX`) AS lTax 
    FROM
      `order header` h 
      INNER JOIN `order detail` d 
