/*!50003 DROP PROCEDURE IF EXISTS `spGetDailySalesPerItemXZ` */;;
/*!50003 SET SESSION SQL_MODE="NO_AUTO_VALUE_ON_ZERO"*/;;
/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`%`*/ /*!50003 PROCEDURE `spGetDailySalesPerItemXZ`(pDate DATE, pTerminalId INT (4))
BEGIN
  SELECT 
    sql1.description,
    sql1.terminalId,
    sql1.shiftId,
    sql1.cashier,
    ROUND(sql1.gross, 2) AS gross,
    ROUND(sql1.itemSold, 0) AS itemSold,
    ROUND(sql1.discount, 2) AS discount,
    ROUND(sql1.sc, 2) AS sc,
    ROUND(sql1.gTax, 2) AS gTax,
    ROUND(sql1.lTax, 2) AS lTax,
    
    ROUND(netSales, 2) AS netSales,
    round(vatSales, 2) as vatSales,
    ROUND(nonVatSales, 2) AS nonVatSales,
    (select 
      h.ORDER_ID 
    FROM
      `order header` h 
      INNER JOIN `order detail` d 
        ON h.`ORDER_ID` = d.`ORDER_ID` 
        AND h.`TERMINAL_ID` = pTerminalId 
        AND h.`STATUS` = 'SERVED' 
        AND d.`OPERATION_STATUS` = 'NEW' 
    WHERE DATE(h.`ORDER_DATE`) = pDate 
    order by h.ORDER_ID asc 
    limit 1) as bOrderId,
    (SELECT 
      h.ORDER_ID 
    FROM
      `order header` h 
      INNER JOIN `order detail` d 
        ON h.`ORDER_ID` = d.`ORDER_ID` 
        AND h.`TERMINAL_ID` = pTerminalId 
        AND h.`STATUS` = 'SERVED' 
        AND d.`OPERATION_STATUS` = 'NEW' 
    WHERE DATE(h.`ORDER_DATE`) = pDate 
    ORDER BY h.ORDER_ID Desc 
    LIMIT 1) AS eOrderId 
  FROM
    (SELECT 
      IF(
        d.`item_id` LIKE 'F%',
        (SELECT 
          description 
        FROM
          `function mapping` 
        WHERE function_id = d.`item_id`),
        ci.`description`
      ) AS description,
      ci.`itemId`,
      h.`TERMINAL_ID` AS terminalId,
      h.`SHIFT_CODE` AS shiftId,
      h.`UPDATEDBY` AS cashier,
      SUM(d.`AMOUNT` + d.`VAT` + d.`LOCAL_TAX` + d.`SERVICE_CHARGE`) AS netSales, 
      sum(d.`AMOUNT` - d.`VAT_EXEMPT_SENIOR`) as vatSales, 
      sum(d.`VAT_EXEMPT_SENIOR`) as nonVatSales, 
      SUM(d.`UNIT_PRICE` * d.`QUANTITY`) AS gross,
      SUM(d.`QUANTITY`) AS itemSold,
      SUM(round(d.`DISCOUNT`,2)) AS discount,
      SUM(d.`SERVICE_CHARGE`) AS sc,
      SUM(d.`VAT`) AS gTax,
      SUM(d.`LOCAL_TAX`) AS lTax 
    FROM
      `order header` h 
      INNER JOIN `order detail` d 
        ON h.`ORDER_ID` = d.`ORDER_ID` 
        AND h.`TERMINAL_ID` = pTerminalId 
        AND h.`STATUS` = 'SERVED' 
        AND d.`OPERATION_STATUS` = 'NEW' 
      LEFT JOIN `common`.`items` ci 
        ON d.`ITEM_ID` = ci.`itemId` 
    WHERE DATE(h.`ORDER_DATE`) = pDate 
    GROUP BY d.`ITEM_ID`) AS sql1 ;
END */;;
