/*!50003 DROP PROCEDURE IF EXISTS `spGetDailySalesRevenue` */;;
/*!50003 SET SESSION SQL_MODE="NO_AUTO_VALUE_ON_ZERO"*/;;
/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `spGetDailySalesRevenue`(
	IN pTerminalId INT(4),
	IN pDate DATE)
BEGIN
	SELECT
	dtl.`ID`,
	dtl.`ORDER_ID`,
	dtl.`UNIT_PRICE`,
	dtl.`ITEM_ID` AS ITEM_ID,
        (if(instr(dtl.`ITEM_ID`, 'F') > 0,(select description from `function mapping` where function_id = dtl.`ITEM_ID`),
		(SELECT `common`.items.description FROM `common`.items WHERE `common`.items.itemId=ITEM_ID))) AS ItemName,
	dtl.`UNIT_PRICE` * dtl.`QUANTITY` as GROSS,
	dtl.`SERVICE_CHARGE`,
	dtl.`VAT`,
	dtl.`LOCAL_TAX`,
	dtl.`QUANTITY`,
	round(dtl.`DISCOUNT`,2) as DISCOUNT,
	(dtl.`AMOUNT` + dtl.`SERVICE_CHARGE` + dtl.`VAT` + dtl.`LOCAL_TAX`) AS NetAmount,
	IF(dtl.IS_FOR_SENIOR = 'False', ROUND((dtl.`DISCOUNT`/ (dtl.`UNIT_PRICE` * dtl.`QUANTITY`)),2) * 100, 
		ROUND(dtl.`DISCOUNT`/(dtl.`DISCOUNT` + dtl.`VAT_EXEMPT_SENIOR`),2) * 100) AS DISCOUNTRATE,
	dtl.`AMOUNT` AS NETPRICE,
	IF((SELECT COUNT(`hotelmgtsystem`.`folio`.`FolioId`) FROM `hotelmgtsystem`.`folio` WHERE `hotelmgtsystem`.`folio`.`FolioId` = hdr.`CUSTOMER_ID`) > 0,
	(SELECT `hotelmgtsystem`.`folio`.`AccountId` FROM `hotelmgtsystem`.`folio` WHERE `hotelmgtsystem`.`folio`.`FolioId` = hdr.`CUSTOMER_ID`),
		IF((SELECT COUNT(`hotelmgtsystem`.`guests`.`AccountId`) FROM `hotelmgtsystem`.`guests` WHERE `hotelmgtsystem`.`guests`.`AccountId` = hdr.`CUSTOMER_ID`) > 0,
		(SELECT `hotelmgtsystem`.`guests`.`AccountId` FROM `hotelmgtsystem`.`guests` WHERE `hotelmgtsystem`.`guests`.`AccountId` = hdr.`CUSTOMER_ID`),
		IF(hdr.`EMPLOYEE_ID` <> '', hdr.`EMPLOYEE_ID`,"WALK-IN"))
	) AS AccountId,
	IF((SELECT COUNT(`hotelmgtsystem`.`folio`.`FolioId`) FROM `hotelmgtsystem`.`folio` WHERE `hotelmgtsystem`.`folio`.`FolioId` = hdr.`CUSTOMER_ID`) > 0,
	(SELECT CONCAT(`hotelmgtsystem`.`guests`.`LastName`,", ", `hotelmgtsystem`.`guests`.`FirstName`) FROM `hotelmgtsystem`.`guests` LEFT JOIN `hotelmgtsystem`.`folio` ON `hotelmgtsystem`.`guests`.`AccountId` = `hotelmgtsystem`.`folio`.`AccountId` WHERE `hotelmgtsystem`.`folio`.`FolioId` = hdr.`CUSTOMER_ID`),
	IF((SELECT COUNT(`hotelmgtsystem`.`guests`.`AccountId`) FROM `hotelmgtsystem`.`guests` WHERE `hotelmgtsystem`.`guests`.`AccountId` = hdr.`CUSTOMER_ID`) > 0,
		(SELECT CONCAT(`hotelmgtsystem`.`guests`.`LastName`,", ", `hotelmgtsystem`.`guests`.`FirstName`) FROM `hotelmgtsystem`.`guests` WHERE `hotelmgtsystem`.`guests`.`AccountId` = hdr.`CUSTOMER_ID`),
		IF(hdr.`EMPLOYEE_ID` <> '', (SELECT CONCAT(employee.`LASTNAME`, ", ", employee.`FIRSTNAME`) FROM employee WHERE employee.`EMPLOYEE_ID` = hdr.`EMPLOYEE_ID`),"WALK-IN CUSTOMER"))
	) AS GuestName
	FROM
	`order detail` dtl
	LEFT JOIN `order header` hdr
	ON dtl.`ORDER_ID` = hdr.`ORDER_ID`
	WHERE DATE(hdr.`ORDER_DATE`) = pDate
	AND dtl.`OPERATION_STATUS` <> 'CANCELLED'		
	AND hdr.`STATUS` = 'SERVED'
	AND hdr.`TERMINAL_ID` = pTerminalId
	order by dtl.ORDER_ID
	;
    END */;;
