/*!50003 SET SESSION SQL_MODE="NO_AUTO_VALUE_ON_ZERO"*/;;
/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `spGetMostSellableItem`(
  pShiftCode varchar (15),
  pPropertyID int,
  pLocationID int,
  pStartDate date,
  pEndDate date
)
BEGIN

  
  
 
 SELECT
  cig.`mainGroupId` AS mainGroupIds,
  IF(INSTR(d.ITEM_ID,'F') > 0,"OTHERS",cig.`groupId`) as groupId,
  ci.`itemId` AS items_id,
  if(instr(d.ITEM_ID,'F') > 0,(SELECT description FROM `function mapping` WHERE function_id = d.`ITEM_ID`),ci.`description`) as description,
  SUM(d.`QUANTITY`) AS soldStocks
  FROM
  `order header` h
  LEFT JOIN `order detail` d
  ON h.`ORDER_ID` = d.`ORDER_ID`
  LEFT JOIN `common`.`items` ci
  ON d.`ITEM_ID` = ci.`itemId`
  LEFT JOIN `common`.`itemlocations` cil
  ON ci.`itemId` = cil.`itemId`
  AND cil.`locationId` = pLocationID
  AND cil.`propertyId` = pPropertyID
  LEFT JOIN `common`.`itemgroups` cig
  ON ci.`groupId` = cig.`groupId`
  WHERE 
  IF(pShiftCode = 'ALL', TRUE, h.`SHIFT_CODE` = pShiftCode)
  AND (DATE(h.`ORDER_DATE`) BETWEEN pStartDate AND pEndDate)
  and d.`OPERATION_STATUS` <> 'CANCELLED'
  AND h.`STATUS` = 'SERVED'
  GROUP BY d.`ITEM_ID`
  ORDER BY cig.`groupId`;
  
  
END */;;
