/*!50003 SET SESSION SQL_MODE="NO_AUTO_VALUE_ON_ZERO"*/;;
/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`%`*/ /*!50003 PROCEDURE `spGetOrderDetailsPerOrder`(
pResto int(5),
pOrderID int(10)
)
BEGIN


select distinct 
`order detail`.id,
`order detail`.order_id,
`order detail`.line_no,
`order detail`.item_id,
`order detail`.description,
`order detail`.unit_price,
`order detail`.quantity,
`order detail`.discount,
`order detail`.vat,
`order detail`.local_tax,
`order detail`.amount,
`order detail`.createdby,
`order detail`.createtime,
`order detail`.updateby,
`order detail`.updatetime,
`order detail`.service_charge,
`order detail`.ditem_id,
`order detail`.route, 
`order detail`.served,
`order detail`.served_by,
`order detail`.time_served,
`order detail`.operation_status,
`order detail`.item_notes,
`order detail`.acknowledge,
`order detail`.acknowledge_by,
`order detail`.time_acknowledged,
`order detail`.is_printed,
`common`.items.description,
`common`.items.unit,
`common`.itemlocations.preparationArea as kitchen_designation,
table_assignment.TABLE_NO
from `order detail` left join `common`.items on `common`.items.itemId = `order detail`.item_id
left join table_assignment on table_assignment.order_id = `order detail`.order_id
left join `common`.itemlocations on `common`.items.itemId = `common`.itemlocations.itemId
where 
`common`.itemlocations.locationId = pResto and 
if(`order detail`.operation_status = 'CANCELLED', !is_printed,`order detail`.OPERATION_STATUS != "") and `order detail`.order_id=pOrderID and if(`order detail`.operation_status = 'CHANGED', !is_printed,`order detail`.OPERATION_STATUS != "") and if(`order detail`.operation_status = 'NEW', !is_printed,`order detail`.OPERATION_STATUS != "")
and (if(`common`.items.isInventoryItems = 'Yes', served < `common`.itemlocations.quantity, `common`.itemlocations.quantity = 0))
order by
`order detail`.createtime
;
END */;;
