/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `spReportDailySummarizedSalesDiscount`(pDate date)
BEGIN


SELECT sql1.*,
	`cash sales` - `total discount` AS net
	FROM
(
SELECT
  cig.`mainGroupId` AS maingroup_id,
