/*!50003 DROP PROCEDURE IF EXISTS `spReportGetDailySalesPerShiftXZ` */;;
/*!50003 SET SESSION SQL_MODE="NO_AUTO_VALUE_ON_ZERO"*/;;
/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`%`*/ /*!50003 PROCEDURE `spReportGetDailySalesPerShiftXZ`(pDate date, pTerminalId int (4))
BEGIN
  SELECT 
    sql2.*,
    round(
      (sql2.netSales + sql2.deposit) - sql2.remitted,
      2
    ) AS cashInDrawer 
  FROM
    (SELECT 
      sql1.*,
      
      round(
        IFNULL(
          (SELECT 
            cl.openingbalance 
          FROM
            `cashiers_logs` cl 
          WHERE cl.transactiondate = pDate 
            AND cl.terminalId = sql1.terminalId 
            AND cl.`type` = 'OPEN' 
            AND cl.shiftcode = sql1.shiftId),
          0
        ),
        2
      ) AS deposit,
      round(
        IFNULL(
          (SELECT 
            cl.amountremitted 
          FROM
            `cashiers_logs` cl 
          WHERE cl.transactiondate = pDate 
            AND cl.terminalId = sql1.terminalId 
            AND cl.`type` = 'CLOSE' 
            AND cl.shiftcode = sql1.shiftId),
          0
        ),
        2
      ) AS remitted 
    FROM
      (SELECT 
        h.`TERMINAL_ID` AS terminalId,
        h.`SHIFT_CODE` AS shiftId,
        h.`UPDATEDBY` AS cashier,
        round(SUM(d.`AMOUNT` + d.`VAT` + d.`LOCAL_TAX` + d.`SERVICE_CHARGE`),2) AS netSales, 
        round(
          SUM(d.`UNIT_PRICE` * d.`QUANTITY`),
          2
        ) AS gross,
        SUM(Round(d.`DISCOUNT`, 2)) AS discount,
        round(SUM(d.`SERVICE_CHARGE`), 2) AS sc,
        round(SUM(d.`VAT`), 2) AS gTax,
        round(SUM(d.`LOCAL_TAX`), 2) AS lTax 
      FROM
        `order header` h 
        INNER JOIN `order detail` d 
          ON h.`ORDER_ID` = d.`ORDER_ID` 
          AND h.`TERMINAL_ID` = pTerminalId 
          AND h.`STATUS` = 'SERVED' 
          AND d.`OPERATION_STATUS` = 'NEW' 
      WHERE DATE(h.`ORDER_DATE`) = pDate 
      GROUP BY shiftId) AS sql1) AS sql2 ;
END */;;
