/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `spReportSummarizedCharges`(
  in pStartDate date,
  in pEndDate date
)
BEGIN
  SELECT 
       Distinct  
    `order header`.order_id,
    `order header`.order_date,
    if(
      `order header`.employee_id = '',
      `hotelmgtsystem`.folioschedules.roomid,
      ''
    ) as roomid,
    if(
      `order header`.employee_id = '',
      concat(
        `hotelmgtsystem`.guests.lastname,
        ', ',
        `hotelmgtsystem`.guests.firstname
      ),
      concat(
        employee.lastname,
        ', ',
        employee.firstname
      )
    ) as `Customer Name`,
    if(
      `order header`.employee_id = '',
      `order header`.customer_id,
      `order header`.employee_id
    ) as customer_id,
    
    vat_sales + NONVAT_SALES AS vat_sales,
    service_charge,
    vat_amount,
    total_amount 
