    left join `hotelmgtsystem`.guests 
      on `hotelmgtsystem`.folio.accountid = `hotelmgtsystem`.guests.accountid,
    payment 
  where `order header`.order_id = payment.order_id 
    and payment.status <> 'VOID' 
    and `order header`.customer_id <> 'WALK-IN' 
    and (
      date(`order header`.ORDER_DATE) >= pStartDate 
      and date(`order header`.ORDER_DATE) <= pEndDate
    ) ;
