/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `spReportSummarizedSalesByOutlet`(
in pMainGroup varchar(30),
in pGroup varchar(50),
in pStartDate date,
in pEndDate date,
in pTerminal int(4)
)
BEGIN


SELECT
hdr.`ORDER_DATE`,
hdr.`ORDER_ID`,
COUNT(DISTINCT hdr.CUSTOMER_ID) AS customer_id, 
dtl.`ITEM_ID`,
itm.`description`,
SUM(dtl.`UNIT_PRICE` * dtl.`QUANTITY`) AS GROSS,
SUM(dtl.`QUANTITY`) AS quantity,
SUM(dtl.`Amount`) AS Nett,
SUM(dtl.`VAT`) AS VAT,
SUM(dtl.`SERVICE_CHARGE`) AS service_charge,
dtl.`CREATEDBY`,
