AND (hdr.ORDER_DATE >= pStartDate 
AND hdr.ORDER_DATE <= pEndDate)
AND dtl.operation_status='NEW'
AND hdr.`STATUS` = 'SERVED' 
GROUP BY grp.groupId, 
	 grp.mainGroupId, 
	 itm.itemId
ORDER BY hdr.order_date;
END */;;
