		`order header` hdr
	on
		dtl.ORDER_ID = hdr.ORDER_ID
	left join
		`common`.items itm
	on
		itm.itemId = dtl.ITEM_ID
	left join
		`common`.itemgroups grp
	on
		grp.groupId = itm.groupId
	
	
	
	
	where
		date(hdr.order_date) >= pStartDate
		and date(hdr.order_date) <= pEndDate
	
	
		and dtl.operation_status != 'CANCELLED'
		and grp.mainGroupId is not null and grp.mainGroupId != ""
		and hdr.TERMINAL_ID = pTerminalId
		and hdr.PostToLedger = 0
		and
		(
			(instr(
				(select GROUP_CONCAT(pmt.PAYMENT_TYPE) as paymentTypes from payment pmt where pmt.ORDER_ID = hdr.ORDER_ID), 
				'CASH'
				) > 0
			)
		or
			(instr(
				(select GROUP_CONCAT(pmt.PAYMENT_TYPE) as paymentTypes from payment pmt where pmt.ORDER_ID = hdr.ORDER_ID), 
				'CREDIT'
				) > 0
			)
			OR
			(INSTR(
				(SELECT GROUP_CONCAT(UCASE(pmt.PAYMENT_TYPE)) AS paymentTypes FROM payment pmt WHERE pmt.ORDER_ID = hdr.ORDER_ID), 
				'ACCOUNT_NONSTAY'
				) > 0
			)
		OR
			(INSTR(
				(SELECT GROUP_CONCAT(UCASE(pmt.PAYMENT_TYPE)) AS paymentTypes FROM payment pmt WHERE pmt.ORDER_ID = hdr.ORDER_ID), 
				'ACCOUNT_EMPLOYEE'
				) > 0
			)
		)
		and
		(select count(pmt.STATUS) as noOfValidPayments from payment pmt where pmt.ORDER_ID = hdr.ORDER_ID and pmt.STATUS != 'VOID') > 0
	)
	union all
	(
	select
		hdr.ORDER_ID,
		hdr.CUSTOMER_ID AS AccountId,
		dtl.ID,
		fmp.METHOD as ITEM_ID,
		dtl.UNIT_PRICE,
		dtl.QUANTITY,
		
		
		
		((dtl.UNIT_PRICE * dtl.QUANTITY) - dtl.DISCOUNT) as NetAmount,
		dtl.DISCOUNT,
		dtl.VAT,
		dtl.LOCAL_TAX,
		dtl.AMOUNT as NetBaseAmount,
		dtl.SERVICE_CHARGE,
		fmp.FUNCTION_ID as MainGroupID,
		dtl.UNIT_PRICE as unitCost
	from
		`order detail` dtl
	left join
		`order header` hdr
	on
		dtl.ORDER_ID = hdr.ORDER_ID
	left join
		`function mapping` fmp
	on
		fmp.FUNCTION_ID = dtl.ITEM_ID
	
	
	
	
	where
		date(hdr.order_date) >= pStartDate
		and date(hdr.order_date) <= pEndDate
	
	
		and dtl.operation_status != 'CANCELLED'
		and fmp.method is not null and fmp.method != ""
	
		and hdr.TERMINAL_ID = pTerminalId
		and hdr.PostToLedger = 0
		and
		(
			(instr(
				(select ucase(GROUP_CONCAT(pmt.PAYMENT_TYPE)) as paymentTypes from payment pmt where pmt.ORDER_ID = hdr.ORDER_ID), 
				'CASH'
				) > 0
			)
			or
			(instr(
				(select ucase(GROUP_CONCAT(pmt.PAYMENT_TYPE)) as paymentTypes from payment pmt where pmt.ORDER_ID = hdr.ORDER_ID), 
				'CREDIT'
				) > 0
			)
			OR
			(INSTR(
				(SELECT GROUP_CONCAT(UCASE(pmt.PAYMENT_TYPE)) AS paymentTypes FROM payment pmt WHERE pmt.ORDER_ID = hdr.ORDER_ID), 
				'ACCOUNT_NONSTAY'
				) > 0
			)
			OR
			(INSTR(
				(SELECT GROUP_CONCAT(UCASE(pmt.PAYMENT_TYPE)) AS paymentTypes FROM payment pmt WHERE pmt.ORDER_ID = hdr.ORDER_ID), 
				'ACCOUNT_EMPLOYEE'
				) > 0
			)
		)
		and
		(select count(pmt.STATUS) as noOfValidPayments from payment pmt where pmt.ORDER_ID = hdr.ORDER_ID and pmt.STATUS != 'VOID') > 0
	)
;
    END */;;
