/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `spGetAccoutTypeReport`(pFrom VARCHAR(30) ,pTo VARCHAR(30),pMarketSegment varchar(50),pClientType varchar(50),pHotelID int(3))
BEGIN
if pMarketSegment <> "ALL" then
	set @_MarketSegment = concat(" AND purpose ='",pMarketSegment,"'");
ELSE
	SET @_MarketSegment = "";
