		FROM
		(
		SELECT 	`value` AS marketSegment,'--------------------------------------------------------' AS companyName,'-------------------------------------------------------' AS  folioID,'' AS `fromDate`,'' AS accountType ,'1' as companyid FROM groupbookingdropdown WHERE groupbookingdropdown.`FieldName`='MarketSegment'
		UNION
		SELECT folio.`purpose` AS marketSegment,(select companyname from company where company.companyid = folio.companyid) AS companyName,folio.`folioID` AS  folioID ,YEAR(folio.`fromDate`) as fromDate,folio.accountType,folio.companyid FROM folio WHERE fromDate BETWEEN '",Concat(pFrom,'-01-01'),"' AND '",CONCAT(pTo,'-12-31'),"' ",@_ClientType, @_MarketSegment,") AS T ORDER BY marketSegment,companyid asc ,fromDate");
PREPARE stmt FROM @scrpt;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
END */;;
