cashiers_logs.terminalid = pTerminalid and 
cashiers_logs.shiftcode = pShiftCode and
cashiers_logs.cashierid = pCashierId and
cashiers_logs.hotelid = pHotelid and
date(cashiers_logs.transactiondate) = pAuditDate and
