order by transactiondate
)
UNION(
select
cashiers_logs.terminalid as Terminal,
cashiers_logs.cashierid,
cashiers_logs.shiftcode,
cashiers_logs.openingbalance,
cashiers_logs.openingadjustment,
cashiers_logs.beginningbalance,
