/*!50003 DROP PROCEDURE IF EXISTS `spReportHistoricalEventsAndRevenue` */;;
/*!50003 SET SESSION SQL_MODE="NO_AUTO_VALUE_ON_ZERO"*/;;
/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `spReportHistoricalEventsAndRevenue`(pFromDate VARCHAR(20), pToDate VARCHAR(20),pType VARCHAR(30),pHotelID INT(4))
BEGIN
SET @ROW =DATE_FORMAT(pToDate,'%Y')-10;
SET @sqlScrptCondition = (SELECT GROUP_CONCAT(T.scrpt) AS scrpt  FROM (SELECT CONCAT("IF( YEAR(FROMDATE)  = '", @ROW , "', 1 ,0) AS x", @ROW,"") AS scrpt , (@ROW := @ROW + 1) AS XROW  FROM MYSQL.`proc` WHERE @ROW <= pToDate LIMIT 11) AS T); 
SET @ROW2 =DATE_FORMAT(pToDate,'%Y')-10;
SET @sqlScrptConditionRev = (SELECT GROUP_CONCAT(T.scrpt) AS scrpt  FROM (SELECT CONCAT("IF( calYear  = '", @ROW2 , "', Revenue ,0) AS x", @ROW2,"") AS scrpt , (@ROW2 := @ROW2 + 1) AS XROW  FROM MYSQL.`proc` WHERE  @ROW2 <= pToDate LIMIT 11) AS T); 
SET @ROW3 =DATE_FORMAT(pToDate,'%Y')-10; 
SET @sqlScrptSum = (SELECT  DISTINCT GROUP_CONCAT(T.scrpt) AS scrpt   FROM (SELECT CONCAT("SUM(x", @ROW3,")"," AS '",@ROW3,"'" ) AS scrpt , (@ROW3 := @ROW3 + 1) AS XROW  FROM MYSQL.`proc` WHERE @ROW3 <=pToDate LIMIT 11) AS T );
IF pType = "EVENTS" THEN
	SET @scrpt = CONCAT("select calmonth as MONTH ,",@sqlScrptSum,"
	
	from (SELECT  date_format(FROMDATE, '%M') as calmonth,
		YEAR(FROMDATE),
		",@sqlScrptCondition,",
		DATE_FORMAT(FROMDATE, '%m') AS MONTH_NDX 	
		from folio as f 
		WHERE f.`Status` NOT IN('NO SHOW', 'CANCELLED')
		AND FROMDATE BETWEEN '",CONCAT(DATE_FORMAT((DATE_SUB(pToDate,INTERVAL 11 YEAR)),"%Y"),"-01-01"),"' AND '", CONCAT(DATE_FORMAT(pToDate,"%Y"),"-12-31"),"') 
	as sql1
	group by calmonth
	ORDER BY MONTH_NDX");
	
ELSEIF pType = "REVENUE" THEN
	SET @scrpt = CONCAT("SELECT 
	calmonth AS MONTH,
	",@sqlScrptSum,"
	FROM 
	(SELECT 
	calmonth,
	",@sqlScrptConditionRev,",
	calYear,
	Revenue,
	MONTH_NDX
	FROM(SELECT  
	DATE_FORMAT(FROMDATE, '%M') AS calmonth,
	YEAR(FROMDATE) AS calYear,
	DATE_FORMAT(FROMDATE, '%m') AS MONTH_NDX ,
	SUM((SELECT SUM(grossAmount) AS amount FROM foliotransactions WHERE foliotransactions.`folioId`=f.`folioID` AND foliotransactions.acctSide='DEBIT'))AS Revenue
	FROM folio AS f 
	WHERE f.`Status` NOT IN('NO SHOW', 'CANCELLED')
	AND FROMDATE BETWEEN '",CONCAT(DATE_FORMAT((DATE_SUB(pToDate,INTERVAL 11 YEAR)),"%Y"),"-01-01"),"' AND '", CONCAT(DATE_FORMAT(pToDate,"%Y"),"-12-31"),"' GROUP BY  calYear, MONTH_NDX  ORDER BY MONTH_NDX ) AS ats
	) AS T
	GROUP BY calmonth
	order by MONTH_NDX");
	
ELSEIF pType = "NOTABLE" THEN
	SET @scrpt = CONCAT("SELECT 
	calYear AS YEAR,
	groupName AS 'EVENT NAME',
	Revenue as REVENUE
	FROM
	(
	SELECT 
	YEAR(FROMDATE) AS calYear,
	f.folioid,
	f.groupName,
	COALESCE(SUM((SELECT SUM(grossAmount) AS amount FROM foliotransactions WHERE foliotransactions.`folioId`=f.`folioID` AND foliotransactions.acctSide='DEBIT')),0)AS Revenue
	,COUNT(f.folioid) AS num
	FROM folio AS f 
	WHERE f.FROMDATE BETWEEN '",CONCAT(DATE_FORMAT((DATE_SUB(pToDate,INTERVAL 11 YEAR)),"%Y"),"-01-01"),"' AND '", CONCAT(DATE_FORMAT(pToDate,"%Y"),"-12-31"),"' 
	GROUP BY f.groupName
	) AS T WHERE T.num>1
	GROUP BY calyear,num
	ORDER BY calyear DESC,T.num DESC
		");
	
	
END IF;
PREPARE stmt FROM @scrpt;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
END */;;
