/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `spReportMemorandum`(pFolioID VARCHAR(30),pFromDate VARCHAR(30),pEndDate VARCHAR(30), pTable TEXT)
BEGIN
	SELECT DISTINCT changes_log.ID,
	SUBSTRING_INDEX(changes_log.REMARKS,":",1) AS REMARKS
	,folio.groupName
	,folio.folioID
	,folioschedules.RoomID
	,rooms.RoomDescription
	,changes_log.OLD_VALUE
	,changes_log.NEW_VALUE
	,folioschedules.activity
	,changes_log.USER_ID
	,changes_log.DATE_CHANGED
	FROM changes_log
	LEFT JOIN folio ON  SUBSTRING_INDEX(changes_log.REMARKS,":",-1)  LIKE CONCAT("%" , folio.folioID ,"%")	
	LEFT JOIN folioschedules ON folioschedules.FolioId = folio.folioID
	LEFT JOIN rooms ON folioschedules.RoomID = rooms.roomid
	WHERE folio.folioID = pFolioID
	AND DATE(DATE_CHANGED) BETWEEN DATE(pFromDate) AND DATE(pEndDate)
	AND  pTable LIKE CONCAT("%",TABLE_NAME,"%") 
	ORDER BY DATE_CHANGED ;
END */;;
