/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `spReportMonthlyExecutive`(pMonth int, pYear int, pHotelID int(2), pSalesExecutive varchar(30))
