/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `spReportTopClients`(pFrom DATE,pTo DATE,pMarketSegment varchar(50),pNumList int(5),pHotelID int(4))
BEGIN
