	LEFT join company on folio.`companyID` = company.`companyid`
	left join guests on folio.`accountID` = guests.`accountid`
	WHERE
	((folio.fromdate BETWEEN pFrom AND pTo) OR
	(folio.todate BETWEEN  pFrom AND pTo)) AND
