roomevents.transferflag = 0 and
(folio.status = 'ONGOING' or folio.status = 'CLOSED') and
( date(folio.fromDate) <= pAuditDate and date(folio.toDate) > pAuditDate) and
rooms.roomtypecode != 'FUNCTION'
