/* Procedure structure for procedure `spSAP_GetNewCustomers` */

DELIMITER $$;

CREATE DEFINER=`root`@`localhost` PROCEDURE `spSAP_GetNewCustomers`(
pExportDate date
)
BEGIN
Select * from
(
(
select 
accountid,
lastname,
firstname,
concat(firstname, ' ',lastname) as ContactPerson,
telephoneno,
faxno,
mobileno,
concat(street,' ',city, ' ',zip, ' ',country) as Address,
createtime
