contactnumber1 as telephoneno,
contactnumber2 as fax,
contactnumber3 as mobile,
concat(street1,' ',city1,' ',zip1,' ',country1) as Address,
createtime
from company
where date(createTime) > pExportDate
)
) customers 
order by createtime asc;
END$$

