return(
select (count(*) / fGetTotalFunctionRooms() * 100) as OccupancyRate
from 
roomevents 
left join folioschedules on folioschedules.folioid = roomevents.eventid
left join rooms on rooms.roomid = roomevents.roomid, roomtypes
