eventdate = pDate and transferFlag = 0 and
(eventType = 'IN HOUSE' or eventType = 'CLOSED') and
if(date(folioschedules.fromDate) = date(folioschedules.toDate),true,
date(folioschedules.toDate) > pDate)
);
